package music.player;
import java.util.List;
import java.util.Scanner;

public class App {
    public String getGreeting() {
        return "Hello World!";
    }

    public static void main(String[] args){
        // loadInformation();
        Account account = new Account(); 
        Ui ui = new Ui(account);
        Methods methods = new Methods(account,ui);
        List<Account> accounts = methods.loadDetails();
        try (Scanner scanner = new Scanner(System.in)) {
            boolean login = false;
            boolean logout = false;
            int choice;
            do {

                System.out.println("-------------------");
                System.out.println("Select your options");            
                ui.printLandingPage();
                choice = scanner.nextInt();
                scanner.nextLine();
                
                switch (choice) {
                    case 1:
                        System.out.println("Enter a email");
                        String email = scanner.nextLine();
                        System.out.println("Enter a password");
                        String password = scanner.nextLine();
                        login = methods.validateDetails(email, password);  
                        
                        if (!login) {
                            System.out.println("Invalid details");
                        } else {
                            methods.splashScreenChoices(scanner);
                        }
                        break;
                    case 2:
                        methods.setAccountDetails(scanner);
                        break;
                    case 3:
                        System.out.println("Exiting App. Have a good day");
                        logout = true;
                        break;
                    default:
                        System.out.println("Select a valid input");
                } 
            } while(logout == false);
            System.out.println("---------------------------");
            System.out.println("Welcome to the Banking App!");
        } 
    }
}
